// Extract prompt from webhook body or use default
const input = $input.first().json;
const prompt = input.body?.prompt || input.prompt || 'a beautiful sunset over mountains, highly detailed, 4k';
const negativePrompt = input.body?.negative_prompt || input.negative_prompt || 'blurry, low quality, distorted';
const seed = input.body?.seed || input.seed || Math.floor(Math.random() * 1000000000);
const steps = input.body?.steps || input.steps || 20;
const cfg = input.body?.cfg || input.cfg || 7;
const width = input.body?.width || input.width || 512;
const height = input.body?.height || input.height || 512;

// ComfyUI API workflow format
// This is a basic txt2img workflow - customize as needed
const comfyWorkflow = {
  &quot;3&quot;: {
    &quot;inputs&quot;: {
      &quot;seed&quot;: seed,
      &quot;steps&quot;: steps,
      &quot;cfg&quot;: cfg,
      &quot;sampler_name&quot;: &quot;euler&quot;,
      &quot;scheduler&quot;: &quot;normal&quot;,
      &quot;denoise&quot;: 1,
      &quot;model&quot;: [&quot;4&quot;, 0],
      &quot;positive&quot;: [&quot;6&quot;, 0],
      &quot;negative&quot;: [&quot;7&quot;, 0],
      &quot;latent_image&quot;: [&quot;5&quot;, 0]
    },
    &quot;class_type&quot;: &quot;KSampler&quot;,
    &quot;_meta&quot;: { &quot;title&quot;: &quot;KSampler&quot; }
  },
  &quot;4&quot;: {
    &quot;inputs&quot;: {
      &quot;ckpt_name&quot;: &quot;v1-5-pruned-emaonly.safetensors&quot;
    },
    &quot;class_type&quot;: &quot;CheckpointLoaderSimple&quot;,
    &quot;_meta&quot;: { &quot;title&quot;: &quot;Load Checkpoint&quot; }
  },
  &quot;5&quot;: {
    &quot;inputs&quot;: {
      &quot;width&quot;: width,
      &quot;height&quot;: height,
      &quot;batch_size&quot;: 1
    },
    &quot;class_type&quot;: &quot;EmptyLatentImage&quot;,
    &quot;_meta&quot;: { &quot;title&quot;: &quot;Empty Latent Image&quot; }
  },
  &quot;6&quot;: {
    &quot;inputs&quot;: {
      &quot;text&quot;: prompt,
      &quot;clip&quot;: [&quot;4&quot;, 1]
    },
    &quot;class_type&quot;: &quot;CLIPTextEncode&quot;,
    &quot;_meta&quot;: { &quot;title&quot;: &quot;CLIP Text Encode (Positive)&quot; }
  },
  &quot;7&quot;: {
    &quot;inputs&quot;: {
      &quot;text&quot;: negativePrompt,
      &quot;clip&quot;: [&quot;4&quot;, 1]
    },
    &quot;class_type&quot;: &quot;CLIPTextEncode&quot;,
    &quot;_meta&quot;: { &quot;title&quot;: &quot;CLIP Text Encode (Negative)&quot; }
  },
  &quot;8&quot;: {
    &quot;inputs&quot;: {
      &quot;samples&quot;: [&quot;3&quot;, 0],
      &quot;vae&quot;: [&quot;4&quot;, 2]
    },
    &quot;class_type&quot;: &quot;VAEDecode&quot;,
    &quot;_meta&quot;: { &quot;title&quot;: &quot;VAE Decode&quot; }
  },
  &quot;9&quot;: {
    &quot;inputs&quot;: {
      &quot;filename_prefix&quot;: &quot;n8n_generated&quot;,
      &quot;images&quot;: [&quot;8&quot;, 0]
    },
    &quot;class_type&quot;: &quot;SaveImage&quot;,
    &quot;_meta&quot;: { &quot;title&quot;: &quot;Save Image&quot; }
  }
};

return {
  json: {
    prompt: comfyWorkflow,
    original_prompt: prompt,
    negative_prompt: negativePrompt,
    seed: seed,
    steps: steps,
    cfg: cfg,
    width: width,
    height: height
  }
};

const response = $input.first().json;
const promptId = response.prompt_id;

if (!promptId) {
  throw new Error('No prompt_id received from ComfyUI');
}

return {
  json: {
    prompt_id: promptId,
    status: 'queued',
    check_count: 0,
    max_checks: 60,
    ...$input.first().json
  }
};

const input = $input.first().json;
const promptId = $('Extract Prompt ID').first().json.prompt_id;
const historyData = input;

// Check if our prompt_id exists in history and has outputs
const promptHistory = historyData[promptId];

if (promptHistory && promptHistory.outputs) {
  // Find the SaveImage node output (node 9 in our workflow)
  const outputs = promptHistory.outputs;
  let images = [];

  for (const nodeId in outputs) {
    if (outputs[nodeId].images) {
      images = images.concat(outputs[nodeId].images);
    }
  }

  if (images.length > 0) {
    return {
      json: {
        status: 'completed',
        prompt_id: promptId,
        images: images,
        image_urls: images.map(img => 
          `http://comfyui:8188/view?filename=${img.filename}&subfolder=${img.subfolder || ''}&type=${img.type || 'output'}`
        ),
        original_prompt: $('Extract Prompt ID').first().json.original_prompt,
        generation_params: {
          seed: $('Extract Prompt ID').first().json.seed,
          steps: $('Extract Prompt ID').first().json.steps,
          cfg: $('Extract Prompt ID').first().json.cfg
        }
      }
    };
  }
}

// Not ready yet - increment check count
const checkCount = ($('Extract Prompt ID').first().json.check_count || 0) + 1;
const maxChecks = $('Extract Prompt ID').first().json.max_checks || 60;

if (checkCount >= maxChecks) {
  throw new Error(`Generation timed out after ${maxChecks} checks`);
}

return {
  json: {
    status: 'pending',
    prompt_id: promptId,
    check_count: checkCount,
    max_checks: maxChecks,
    original_prompt: $('Extract Prompt ID').first().json.original_prompt,
    seed: $('Extract Prompt ID').first().json.seed,
    steps: $('Extract Prompt ID').first().json.steps,
    cfg: $('Extract Prompt ID').first().json.cfg
  }
};
